import Description from "./Description";
import React, { useEffect, useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { useContext } from "react";
import CartContext from "./cart-context";

const MenuOption = (props) => {
  const [amount, setAmount] = useState('1');
  const [amountIsValid, setAmountIsValid] = useState(true);

  const amountOnChangeHandler = (event) => {
    console.log(event.target.value);

    setAmount(event.target.value);
  };

  useEffect(() => {
    console.log("hello");
    if(isNaN(amount) || amount.trim().length === 0)
    {
      console.log("not a number")
      setAmountIsValid(false);
    }
    else
    {
      console.log("is a number")
      setAmountIsValid(true);
    }
  },[amount])

  const onItemAddedHandler = (event) =>{
      event.preventDefault();
      ctx.addCartItems(props.foodName, props.price, +amount)
  }

  const ctx = useContext(CartContext);

  return (
    <React.Fragment>
      <form onSubmit={onItemAddedHandler}>
        <Container
          style={{
            backgroundColor: "#da9b86",
            borderRadius: "5px",
            marginTop: "10px",
          }}
        >
          <Row>
            <Col>
              <Description
                foodName={props.foodName}
                description={props.description}
                price={props.price}
              ></Description>
            </Col>
            <Col style={{ marginTop: "20px" }}>
              <Row>
                <Col>
                  <input
                    style={{
                      backgroundColor: amountIsValid ? 'white' : ' red',
                      borderRadius: "3px",
                      border: "none",
                      width: "50px",
                      float: "right",
                      marginLeft: "20px",
                    }}
                    value={amount}
                    onChange={amountOnChangeHandler}
                  ></input>
                  <div style={{ float: "right" }}>Amount</div>
                </Col>
              </Row>
              <Row>
                <Col>
                  <button
                    style={{
                      border: "none",
                      color: "white",
                      float: "right",
                      backgroundColor: "#FF0000",
                      borderRadius: "15px",
                      width: "120px",
                      height: "25px",
                      fontSize: "16px",
                      marginTop: "10px",
                    }} type='submit'
                  >
                    {" "}
                    +Add{" "}
                  </button>
                </Col>
              </Row>
            </Col>
          </Row>
          <Row></Row>
        </Container>
      </form>
    </React.Fragment>
  );
};

export default MenuOption;
