import { useEffect , useState} from "react";
import Card from "react-bootstrap/Card";
import MenuOption from "./MenuOption";

const Menu = (props) => {
  
    const menuItems = [
        {
            foodName: 'Sushi',
            description: 'Finest fish and veggies',
            price: 22.99
        },
        {
            foodName: 'Schnitzel',
            description: 'A german specialty!',
            price: 16.50
        },
        {
            foodName: 'Adana Kebap',
            description: 'Efsane Adana Kebap',
            price: 18.00
        }
    ]

    const [cartItems,setCartItems] = useState([]);

    const addCartItems = (itemName, itemPrice, itemAmount) => {
        const newItems = {key: Math.random(), foodName: itemName, price:itemPrice, amount: itemAmount}
        let isNewItem=true;
        setCartItems( (prev)=>{

            let found = false;
            prev.map((item) => {
                if(item.foodName == itemName)
                {
                    item.amount = +item.amount + +itemAmount;
                    found=true;
                }
            })

            if(!found)
            {
                return [newItems, ...prev];
            }
            else{
                return [...prev];
            }
        })
    }

    useEffect(() => {
        //console.log("use effect");
        //console.log(cartItems);
    })

return (
    <Card
      style={{
        width: "800px",
        margin: "auto",
        marginTop: "50px",
        padding: "20px",
        marginBottom: "50px",
        backgroundColor: "#ccc",
      }}
    >
    {menuItems.map((item) => <MenuOption key={Math.random()} addCartItems = {addCartItems} foodName={item.foodName} description={item.description} price={item.price}></MenuOption>)}
      
    </Card>
  );
};

export default Menu;
