import styles from "./Navigation.module.css";
import { Card, Row, Col, Container } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import React, { useContext, useState } from "react";
import Cart from './Cart.js'
import './Cart.css'
import ReactDOM from 'react-dom';
import CartContext from "./cart-context";

const CartButton = () => {
  const [cartWindowOn, setCartWindow] = useState(false);
  const ctx = useContext(CartContext);

  const cartBtnClicked = () => {
    setCartWindow(true);
  };

  const overlayClicked = () => {
      setCartWindow(false);
  }

  const cartRoot = document.getElementById('cart-root');

  return (
    <React.Fragment>
        {cartWindowOn && ReactDOM.createPortal(<div onClick={overlayClicked} className={'overlay'}>
          <Cart></Cart>
          </div>, cartRoot) }
      <Card>
        <button onClick={cartBtnClicked} style={{border: 'none'}}>
          <Container>
            <Row>
              <Col>
                <img
                  src="https://www.freepnglogos.com/uploads/shopping-cart-png/shopping-cart-svg-png-icon-download-28.png"
                  alt="Italian Trulli"
                  style={{ width: "20px", height: "20px" }}
                />
              </Col>

              <Col>
                <div style={{width: '80px'}} className={styles.cart}>Your Cart</div>
              </Col>
              <Col>
                <div style={{lineHeight: '0px', fontSize: '20px',position:'relative',top: '50%'}}>{ctx.cartState.totalNumberOfItems}</div>
              </Col>
            </Row>
          </Container>
        </button>
      </Card>
    </React.Fragment>
  );
};

export default CartButton;
