import Description from "./Description";
import React, { useState } from "react";
import { Container, Row, Col } from "react-bootstrap";

const MenuOption = (props) => {

    const[amount, setAmount] = useState(1);
    const amountOnChangeHandler = (event) => {
        console.log(event.target.value);
        setAmount(event.target.value);
    }



  return (
    <React.Fragment>
        <form>
        <Container style={{ backgroundColor: '#AAC',borderRadius: '5px' ,marginTop: "10px" }}>
        <Row>
          <Col>
            <Description foodName={props.foodName} description={props.description} price={props.price}></Description>
          </Col>
          <Col style={{ marginTop: "20px" }}>
            <Row>
              <Col>
                <input
                  style={{
                    borderRadius: "3px",
                    border: "none",
                    width: "50px",
                    float: "right",
                    marginLeft: "20px",
                  }} value={amount} onChange={amountOnChangeHandler}
                ></input>
                <div style={{ float: "right" }}>Amount</div>
              </Col>
            </Row>
            <Row>
              <Col>
                <button
                  style={{
                    border: "none",
                    color: "white",
                    float: "right",
                    backgroundColor: "#FF0000",
                    borderRadius: "15px",
                    width: "120px",
                    height: "25px",
                    fontSize: "16px",
                    marginTop: "10px",
                  }}
                >
                  {" "}
                  +Add{" "}
                </button>
              </Col>
            </Row>
          </Col>
        </Row>
        <Row></Row>
      </Container>
        </form>
    </React.Fragment>
  );
};

export default MenuOption;
