import { useEffect , useState} from "react";
import Card from "react-bootstrap/Card";
import MenuOption from "./MenuOption";

const Menu = (props) => {
  
    const menuItems = [
        {
            foodName: 'Sushi',
            description: 'Finest fish and veggies',
            price: 22.99
        },
        {
            foodName: 'Schnitzel',
            description: 'A german specialty!',
            price: 16.50
        },
        {
            foodName: 'Adana Kebap',
            description: 'Efsane Adana Kebap',
            price: 18.00
        }
    ]

    const [cartItems,setCartItems] = useState([]);

    const addCartItems = (itemName, itemPrice, itemAmount) => {
        const newItems = {name: itemName, price:itemPrice, amount: itemAmount}

        const isNewItem=true;
        setCartItems( (prev)=>{

            prev.map((item) =>{
                if(item.foodName === newItems.foodName)
                {
                    item.count = item.count + newItems.amount;
                    isNewItem=false;
                }
            })

            if(isNewItem)
                return [newItems, ...prev];
        })
    }

    useEffect(() => {
        console.log(cartItems);
    })

return (
    <Card
      style={{
        width: "800px",
        margin: "auto",
        marginTop: "50px",
        padding: "20px",
        marginBottom: "50px",
        backgroundColor: "#ccc",
      }}
    >
    {menuItems.map((item) => <MenuOption addCartItems = {addCartItems} foodName={item.foodName} description={item.description} price={item.price}></MenuOption>)}
      
    </Card>
  );
};

export default Menu;
