import { Card } from "react-bootstrap"
import CartItem from "./CartItem"
import CartContext from "./cart-context"
import { useContext } from "react"

const Cart = () => {

    const ctx = useContext(CartContext);

    return (
        <Card style={{margin: 'auto', width: '500px',marginTop: '100px', height: '300px'}}>
            {ctx.cartItems.map( (item) => <CartItem foodName={item.foodName} price={item.price} amount={item.amount}/>)}
        </Card>
    )
}

export default Cart
