import { Card } from "react-bootstrap";
import CartItem from "./CartItem";
import CartContext from "../cart-context";
import { useContext } from "react";
import { Container, Row, Col } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import styles from './Cart.module.css'

const Cart = () => {
  const ctx = useContext(CartContext);

  const onCardClick = (event) => {
    event.stopPropagation();
  };

  return (
    <Card onClick={onCardClick} className={styles.cart} style={{padding: '10px', backgroundColor: '#F5F5F5'}}>
      <Card style={{ overflowY: "scroll", minHeight: "240px",maxHeight: "240px"}}>
        <ul>
          {ctx.cartState.cartItems.map((item) => (
            <CartItem
              key={Math.random()}
              foodName={item.foodName}
              price={item.price}
              amount={item.amount}
            />
          ))}
        </ul>
      </Card>
      <div>
      <Container style={{ position: "absolute", bottom: "0"}}>
        <Row className="justify-content-around">
          <Col md="auto">
            <h4>Total Price:</h4>
          </Col>
          <Col md="auto">
            <h4>{ctx.cartState.cartTotalPrice.toFixed(2)}</h4>
          </Col>
          <Col md="auto">
            <button className="btn btn-primary">Order</button>
          </Col>
          <Col md="auto" style={{marginRight: "10px"}}>
            <button className="btn btn-primary">Close</button>
          </Col>
        </Row >
        <Row className="justify-content-end" style={{marginBottom: "10px"}}>
          
        </Row>
      </Container>
      </div>
    </Card>
  );
};

export default Cart;
