import { Card } from "react-bootstrap";
import CartItem from "./CartItem";
import CartContext from "./cart-context";
import { useContext } from "react";
import { Container, Row, Col } from "react-bootstrap";

const Cart = () => {
  const ctx = useContext(CartContext);

  const onCardClick = (event) => {
    event.stopPropagation();
  };

  return (
    <Card
      onClick={onCardClick}
      style={{
        margin: "auto",
        width: "500px",
        marginTop: "100px",
        height: "300px",
      }}
    >
      {ctx.cartState.cartItems.map((item) => (
        <CartItem
          key={Math.random()}
          foodName={item.foodName}
          price={item.price}
          amount={item.amount}
        />
      ))}

      <Container>
        <Row style={{ position: "absolute", bottom: "0"}}>
          <Col style={{width: '270px', marginRight: '0px'}}>
            <h4>Total Price:&nbsp;&nbsp;</h4>
          </Col>
          <Col style={{marginLeft: '0px'}}>
            <h4>{ctx.cartState.cartTotalPrice}</h4>
          </Col>
        </Row>
        
      </Container>
    </Card>
  );
};

export default Cart;
