import styles from "./Navigation.module.css";
import { Card, Row, Col, Container } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import React, { useState } from "react";
import Cart from './Cart.js'
import './Cart.css'
import { ReactDOM } from "react-dom";

const CartButton = () => {
  const [cartWindowOn, setCartWindow] = useState(false);

  const cartBtnClicked = () => {
    setCartWindow(true);
  };

  const overlayClicked = () => {
      setCartWindow(false);
  }

  const cartRoot = document.getElementById('cart-root');

  return (
    <React.Fragment>
        {cartWindowOn && ReactDOM.createPortal(<div onClick={overlayClicked} className={'overlay'}>
          <Cart></Cart>
          </div>, cartRoot) }
      <Card>
        <button onClick={cartBtnClicked}>
          <Container>
            <Row>
              <Col>
                <img
                  src="https://media.istockphoto.com/vectors/shopping-cart-icon-isolated-on-white-background-vector-id1206806317?s=612x612"
                  alt="Italian Trulli"
                  style={{ width: "40px", height: "40px" }}
                />
              </Col>

              <Col>
                <div className={styles.cart}>Your Cart</div>
              </Col>
            </Row>
          </Container>
        </button>
      </Card>
    </React.Fragment>
  );
};

export default CartButton;
