import { Container, Row, Col,Card } from "react-bootstrap";
import React, { useContext } from "react";
import CartContext from "./cart-context";

const CartItem = (props) => {

  const ctx = useContext(CartContext);

  const increaseAmount = () => {
    ctx.addCartItems(props.foodName, props.price, 1)
  }
  
  const decreaseAmount = () => {
    ctx.removeItem()
  }
    
  return (
    <React.Fragment>
      <Container style={{borderBottom: '2px solid red'}}>
        <Row>
            <Col>
                <h4>{props.foodName}</h4>
            </Col>

            <Col>
            <div style={{marginTop: '15px'}}>
                <button onClick={increaseAmount} style={{float: 'right', marginLeft:'10px',borderStyle: 'solid', borderRadius: '5px' , fontSize:'15px', width:'40px',height:'20px', lineHeight:'0px'}}>+</button>
                <button onClick={decreaseAmount} style={{float: 'right', fontSize:'15px',borderStyle: 'solid', borderRadius: '5px' , width:'40px',height:'20px', lineHeight:'0px'}}>-</button>
            </div>
            </Col>
        </Row>
        
        <Row style={{marginBottom: '5px'}}> 
            <div style={{display: 'flex'}}>
                <div style={{float: 'left'}}>
                {props.price}
                </div>
                
                <Card style={{float: 'left', marginLeft: '80px'}}>
                <div >x {props.amount}</div>
                </Card>
            </div>
        </Row>
      </Container>
    </React.Fragment>
  );
};

export default CartItem;
