import { Card } from "react-bootstrap";
import CartItem from "./CartItem";
import CartContext from "../cart-context";
import React, { useContext } from "react";
import { Container, Row, Col } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import styles from './Cart.module.css'

const Cart = () => {
  const ctx = useContext(CartContext);

  const onCardClick = (event) => {
    event.stopPropagation();
  };

  return (
    <React.Fragment>

    <Card onClick={onCardClick} className={styles.cart} style={{padding: '10px', backgroundColor: '#F5F5F5',overflowY: "scroll"}}>
      <ul>
        {ctx.cartState.cartItems.map((item) => (
          <CartItem
            key={Math.random()}
            foodName={item.foodName}
            price={item.price}
            amount={item.amount}
          />
        ))}
      </ul>
    </Card>
    <div>
    <Container style={{ position: "absolute", bottom: "0",margin:"auto"}}>
      <Row className="justify-content-start">
        <Col md="auto">
          <h4>Total Price:</h4>
        </Col>
        <Col md="auto">
          <h4>{ctx.cartState.cartTotalPrice.toFixed(2)}</h4>
        </Col>
      </Row >
      <Row className="justify-content-end" style={{marginBottom: "10px"}}>
        <Col md="auto">
          <button className="btn btn-primary">Order</button>
        </Col>
        <Col md="auto" style={{marginRight: "10px"}}>
          <button className="btn btn-primary">Close</button>
        </Col>
      </Row>
    </Container>
    </div>
    </React.Fragment>
  );
};

export default Cart;
