import React from "react"
import { propTypes } from "react-bootstrap/esm/Image"

const Description = (props) => {

    return(
        <React.Fragment>
            <h4>{props.foodName}</h4>
            <div>{props.description}</div>
            <p >Price: {props.price}</p>
        </React.Fragment>
    )
}

export default Description
