import { Card } from "react-bootstrap"
import CartItem from "./CartItem"
import CartContext from "./cart-context"
import { useContext } from "react"

const Cart = () => {

    const ctx = useContext(CartContext);

    const onCardClick = (event) =>{
        event.stopPropagation();
      }
    return (
        <Card onClick={onCardClick} style={{margin: 'auto', width: '500px',marginTop: '100px', height: '300px'}}>
            {ctx.cartItems.map( (item) => <CartItem key={Math.random()} foodName={item.foodName} price={item.price} amount={item.amount}/>)}
        </Card>
    )
}

export default Cart
