import logo from "./logo.svg";
import "./App.css";
import Navigation from "./components/Navigation";
import "bootstrap/dist/css/bootstrap.min.css";
import Card from "react-bootstrap/Card";
import React, { useEffect, useReducer } from "react";
import IntroText from "./components/IntroText";
import Menu from "./components/Menu";
import CartContext from "./components/cart-context";
import Cart from "./components/CartButton";
import { useState, useContext } from "react";

const cartReducer = (state, action) => {
  if (action.type === "NEW_ITEM_ADDED") {
    let found = false;
    state.cartItems.map((item) => {
      if (item.foodName == action.value.itemName) {
        item.amount = +item.amount + +action.value.amount;
        found = true;
      }
    });

    if (!found) {
      const newArray = [action.value, ...state]
      return [action.value, ...state];
    } else {
      return [...state];
    }
  }
};

function App() {
  const cartContext = useContext(CartContext);

  const [cartItems, setCartItems] = useState([]);
  const [cartTotalPrice, setCartTotalPrice] = useState(0);

  const initialState = { cartItems: [], cartTotalPrice: 0 };

  const [cartState, cartDispatch] = useReducer(cartReducer, initialState);

  const addCartItems = (itemName, itemPrice, itemAmount) => {
    const newItems = {
      key: Math.random(),
      foodName: itemName,
      price: itemPrice,
      amount: itemAmount,
    };
    let isNewItem = true;
    cartDispatch({ type: "NEW_ITEM_ADDED", value: newItems });
  };

  useEffect(() => {
    console.log(cartItems);
  });

  return (
    <React.Fragment>
      <CartContext.Provider
        value={{ addCartItems: addCartItems, cartItems: cartItems }}
      >
        <div style={{ fontFamily: "sans-serif", backgroundColor: "black" }}>
          <Navigation />
          <IntroText />
          <Menu />
        </div>
      </CartContext.Provider>
    </React.Fragment>
  );
}

export default App;
