import { useEffect , useState, useContext} from "react";
import Card from "react-bootstrap/Card";
import MenuOption from "./MenuOption";
import CartContext from "./cart-context";

const Menu = (props) => {
  
    const menuItems = [
        {
            foodName: 'Sushi',
            description: 'Finest fish and veggies',
            price: 22.99
        },
        {
            foodName: 'Schnitzel',
            description: 'A german specialty!',
            price: 16.50
        },
        {
            foodName: 'Adana Kebap',
            description: 'Efsane Adana Kebap',
            price: 18.00
        }
    ]

    const ctx = useContext(CartContext);

    useEffect(() => {
        //console.log("use effect");
        //console.log(cartItems);
    })



return (
    <Card
      style={{
        width: "800px",
        margin: "auto",
        marginTop: "50px",
        padding: "20px",
        marginBottom: "50px",
        backgroundColor: "#ccc",
      }}
    >
    {menuItems.map((item) => <MenuOption key={Math.random()} addCartItems = {ctx.addCartItems} foodName={item.foodName} description={item.description} price={item.price}></MenuOption>)}
      
    </Card>
  );
};

export default Menu;
