import Description from "./Description";
import React from "react";
import { Container, Row, Col } from "react-bootstrap";

const MenuOption = () => {
  return (
    <React.Fragment>
      <Container style={{ backgroundColor: 'white',borderRadius: '5px' ,marginTop: "10px" }}>
        <Row>
          <Col>
            <Description></Description>
          </Col>
          <Col style={{ marginTop: "20px" }}>
            <Row>
              <Col>
                <input
                  style={{
                    borderRadius: "3px",
                    border: "none",
                    width: "50px",
                    float: "right",
                    marginLeft: "20px",
                  }}
                ></input>
                <div style={{ float: "right" }}>Amount</div>
              </Col>
            </Row>
            <Row>
              <Col>
                <button
                  style={{
                    border: "none",
                    color: "white",
                    float: "right",
                    backgroundColor: "#FF0000",
                    borderRadius: "15px",
                    width: "120px",
                    height: "25px",
                    fontSize: "16px",
                    marginTop: "10px",
                  }}
                >
                  {" "}
                  +Add{" "}
                </button>
              </Col>
            </Row>
          </Col>
        </Row>
        <Row></Row>
      </Container>
    </React.Fragment>
  );
};

export default MenuOption;
